mapper.insert:
INSERT INTO TBL_BOARD (
	BON
	, TITLE
	, CONTENT
	, WRITER
) VALUES (
	SEQ_BOARD.NEXTVAL
	, #{title}
	, #{content}
	, #{writer}
)


	SELECT
		SEQ_BOARD.NEXTVAL FROM DUAL

INSERT INTO TBL_BOARD (
	BON
	, TITLE
	, CONTENT
	, WRITER
) VALUES (
	#{bon}
	, #{title}
	, #{content}
	, #{writer}
)

mapper.update:
UPDATE TBL_BOARD
SET
	TITLE = #{title}
	, CONTENT = #{content}
	, WRITER = #{writer}
	, UPDATEDATE = sysdate
WHERE
	BON = #{bon}
